mapper.update:
update book
code = #{code}, name = #{name}, author = #{author}, press = #{press}, date_press = #{datePress}, isbn = #{isbn}, catalog = #{catalog}, language = #{language}, pages = #{pages}, price = #{price}, brief = #{brief}, cover = #{cover}, status = #{status}
where id = #{id}

mapper.select:
select * from book


        where id = #{id}



            and code = #{code}
            and name = #{name}
            and author = #{author}
            and press = #{press}
            and isbn = #{isbn}
            and catalog = #{catalog}
            and language = #{language}
            and date_in = #{dateIn}
            and status = #{status}
